const BASE64_CHARS =
  'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/='

const fontBitsBuffer: FontBitsBuffer = {}

function doubleScale(rgbaSource: Uint8Array, fontWidth: number): Uint8Array {
  const byteWidth = fontWidth * 4
  const doubledByteWidth = byteWidth * 2
  const rgbaDoubled = new Uint8Array(rgbaSource.length * 4)

  for (let i = 0, k = 0; i < rgbaSource.length; i += 4) {
    const rgba = rgbaSource.subarray(i, i + 4)
    rgbaDoubled.set(rgba, k)
    k += 4
    rgbaDoubled.set(rgba, k)
    k += 4
    if ((i + 4) % byteWidth === 0) {
      const startOfRow = k - doubledByteWidth
      rgbaDoubled.set(
        rgbaDoubled.subarray(startOfRow, startOfRow + doubledByteWidth),
        k,
      )
      k += doubledByteWidth
    }
  }
  return rgbaDoubled
}

function font(
  bits: Uint8Array,
  width: number,
  height: number,
  amigaFont: boolean,
  options: RenderOptions,
): Font {
  const fontBitWidth = width * height
  const fontBuffer: Record<number, Uint8Array> = {}
  const excludeNinthBit =
    (width === 9 && options.bits !== '9') || (width === 9 && options.thumbnail)
  const fontBuffer24Bit = new Uint8Array(
    (excludeNinthBit ? width - 1 : width) * height * 4,
  )

  function getData(
    charCode: number,
    palette: Uint8Array[],
    fg: number,
    bg: number,
  ): Uint8Array {
    const bufferIndex = charCode + (fg << 8) + (bg << 12)

    if (!fontBuffer[bufferIndex]) {
      fontBuffer[bufferIndex] = new Uint8Array(
        (excludeNinthBit ? width - 1 : width) * height * 4,
      )

      for (
        let i = 0, j = charCode * fontBitWidth, k = 0;
        i < fontBitWidth;
        ++i, ++j
      ) {
        if (!excludeNinthBit || (i + 1) % 9 !== 0) {
          if (bits[j]) {
            fontBuffer[bufferIndex].set(palette[fg], k)
          } else {
            if (
              amigaFont &&
              fg > 7 &&
              i % width > 0 &&
              bits[j - 1] &&
              (options.bits === 'ced' || options.bits === 'workbench')
            ) {
              fontBuffer[bufferIndex].set(palette[fg], k)
            } else {
              fontBuffer[bufferIndex].set(palette[bg], k)
            }
          }
          k += 4
        }
      }
      if (options['2x']) {
        fontBuffer[bufferIndex] = doubleScale(
          fontBuffer[bufferIndex],
          excludeNinthBit ? width - 1 : width,
        )
      }
    }
    return fontBuffer[bufferIndex]
  }

  function get24BitData(
    charCode: number,
    fg: Uint8Array,
    bg: Uint8Array,
  ): Uint8Array {
    for (
      let i = 0, j = charCode * fontBitWidth, k = 0;
      i < fontBitWidth;
      ++i, ++j
    ) {
      if (!excludeNinthBit || (i + 1) % 9 !== 0) {
        if (bits[j]) {
          fontBuffer24Bit.set(fg, k)
        } else {
          fontBuffer24Bit.set(bg, k)
        }
        k += 4
      }
    }
    if (options['2x']) {
      return doubleScale(fontBuffer24Bit, excludeNinthBit ? width - 1 : width)
    }
    return fontBuffer24Bit
  }

  return {
    getData,
    get24BitData,
    height: height * (options['2x'] ? 2 : 1),
    width: (excludeNinthBit ? 8 : width) * (options['2x'] ? 2 : 1),
  }
}

function bytesToBits(file: FileObj, width: number, height: number): Uint8Array {
  const bits = new Uint8Array(width * height * 256)

  for (let i = (width * height * 256) / 8, k = 0; i > 0; --i) {
    const v = file.get()
    for (let j = 7; j >= 0; --j) {
      bits[k++] = (v >> j) & 1 ? 1 : 0
    }
  }
  return bits
}

function base64ToFile(text: string): FileObj {
  const bytes16 = new Uint32Array(1)
  const bytes8 = new Uint8Array((text.length / 4) * 3)

  for (let i = 0, j = 0; i < text.length; bytes16[0] = 0) {
    bytes16[0] += (BASE64_CHARS.indexOf(text.charAt(i++)) & 63) << 18
    bytes16[0] += (BASE64_CHARS.indexOf(text.charAt(i++)) & 63) << 12
    bytes16[0] += (BASE64_CHARS.indexOf(text.charAt(i++)) & 63) << 6
    bytes16[0] += BASE64_CHARS.indexOf(text.charAt(i++)) & 63
    bytes8[j++] = (bytes16[0] >> 16) & 255
    bytes8[j++] = (bytes16[0] >> 8) & 255
    bytes8[j++] = bytes16[0] & 255
  }
  return new File(bytes8)
}

function preset(name: string, options: RenderOptions): Font {
  switch (name) {
    case 'amiga':
      name = 'topaz'
      break
    case 'microknightplus':
      name = 'microknight+'
      break
    case 'topazplus':
      name = 'topaz+'
      break
    case 'topaz500plus':
      name = 'topaz500+'
      break
  }

  if (!fontBitsBuffer[name]) {
    fontBitsBuffer[name] = bytesToBits(
      base64ToFile(FONT_PRESETS[name].data),
      FONT_PRESETS[name].width,
      FONT_PRESETS[name].height,
    )
  }

  return font(
    fontBitsBuffer[name],
    FONT_PRESETS[name].width,
    FONT_PRESETS[name].height,
    FONT_PRESETS[name].amigaFont,
    options,
  )
}

function xbin(file: FileObj, fontHeight: number, options: RenderOptions): Font {
  return font(bytesToBits(file, 8, fontHeight), 8, fontHeight, false, options)
}

function font8x16x256(file: FileObj, options: RenderOptions): Font {
  return font(bytesToBits(file, 8, 16), 8, 16, false, options)
}

function has(name: string): boolean {
  switch (name) {
    case 'amiga':
    case 'microknightplus':
    case 'topazplus':
    case 'topaz500plus':
      return true
    default:
      return Object.prototype.hasOwnProperty.call(FONT_PRESETS, name)
  }
}

export const FontModule = {
  preset,
  xbin,
  font8x16x256,
  has,
}
